select `i`.`id` AS `inventor_id`,`i`.`name_first` AS `name_first`,`i`.`name_last` AS `name_last`,`l`.`id` AS `location_id`,`l`.`city` AS `city`,`l`.`state` AS `state`,`l`.`country` AS `country`,`l`.`latitude` AS `latitude`,`l`.`longitude` AS `longitude` from ((`inventor` `i` left join `location_inventor` `li` on((`i`.`id` = `li`.`inventor_id`))) left join `location` `l` on((`li`.`location_id` = `l`.`id`)))